import { useState, useEffect, createContext, useContext } from 'react';
import { supabase } from '../utils/supabaseClient';

const AuthContext = createContext(null);

export const AuthProvider = ({ children }) => {
  const [session, setSession] = useState(null);
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);

  // Función para obtener el perfil del usuario
  const getProfile = async (user) => {
    if (!user) return null;
    const { data, error } = await supabase
      .from('profiles')
      .select('*')
      .eq('id', user.id)
      .single();
    if (error) {
      console.error('Error fetching profile:', error);
      return null;
    }
    return data;
  };

  useEffect(() => {
    // "Temporizador de seguridad": si la verificación de la sesión tarda más de 5 segundos,
    // forzamos el fin de la carga para evitar que la aplicación se quede "pegada".
    const safetyTimer = setTimeout(() => {
      if (loading) {
        console.warn("La verificación de la sesión tardó demasiado. Forzando el fin de la carga.");
        setLoading(false);
      }
    }, 5000); // 5 segundos de tiempo de espera

    const fetchInitialSession = async () => {
      try {
        const { data: { session: currentSession }, error: sessionError } = await supabase.auth.getSession();
        if (sessionError) throw sessionError;

        setSession(currentSession);
        if (currentSession) {
          const userProfile = await getProfile(currentSession.user);
          setProfile(userProfile);
        }
      } catch (e) {
        console.error("Error al inicializar la sesión de autenticación:", e);
      } finally {
        // Cuando la carga termina (con éxito o error), limpiamos el temporizador y finalizamos la carga.
        clearTimeout(safetyTimer);
        setLoading(false);
      }
    };

    fetchInitialSession();

    const { data: { subscription } } = supabase.auth.onAuthStateChange(async (_event, newSession) => {
      setSession(newSession);
      const userProfile = newSession ? await getProfile(newSession.user) : null;
      setProfile(userProfile);
    });

    // Función de limpieza para cuando el componente se desmonte.
    return () => {
      subscription.unsubscribe();
      clearTimeout(safetyTimer);
    };
  }, []);

  const value = { session, profile, loading };

  // Si la sesión inicial aún se está cargando, mostramos un loader global.
  // Esto previene que el resto de la app se renderice con un estado de autenticación
  // incorrecto (ej. profile=null) durante un refresco de página.
  // Soluciona el problema de los botones que desaparecen y los datos que no cargan.
  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-100">
        <div className="text-xl font-semibold text-gray-700">Verificando sesión...</div>
      </div>
    );
  }

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};
