import React, { useState, useEffect, useCallback } from 'react';
import { motion } from 'framer-motion';
import { LayoutDashboard, Users, Package, Settings, LogOut, Image as ImageIcon } from 'lucide-react';
import { supabase } from '../utils/supabaseClient'; // Importar el cliente de Supabase
import { useNavigate } from 'react-router-dom';

// Importar los nuevos componentes de gestión
import DashboardOverview from '../components/admin/DashboardOverview';
import UserManagement from '../components/admin/UserManagement';
import SoftwareManagement from '../components/admin/SoftwareManagement';
import ServiceManagement from '../components/admin/ServiceManagement';
import SiteContentManagement from '../components/admin/SiteContentManagement';

const AdminDashboard = () => {
  const [activeTab, setActiveTab] = useState('overview');
  const [users, setUsers] = useState([]); // Este estado podría moverse al componente UserManagement
  const [softwareProducts, setSoftwareProducts] = useState([]);
  const [servicesOffered, setServicesOffered] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const checkUserAndFetchData = async () => {
      setLoading(true);
      setError(null);
      try {
        // Verificar si el usuario es admin (esto es una simulación, la lógica real es más compleja)
        const { data: { user } } = await supabase.auth.getUser();
        if (!user || user.email !== 'admin@iqgeospatial.com') { // ¡Ajusta esto a tu lógica de rol!
          navigate('/login'); // Redirigir si no es admin
          return;
        }

        const { data: usersData, error: usersError } = await supabase.from('users').select('*');
        if (usersError) throw usersError;
        setUsers(usersData);

        const { data: softwareData, error: softwareError } = await supabase.from('software_products').select('*');
        if (softwareError) throw softwareError;
        setSoftwareProducts(softwareData);

        const { data: servicesData, error: servicesError } = await supabase.from('services_offered').select('*');
        if (servicesError) throw servicesError;
        setServicesOffered(servicesData);

      } catch (err) {
        setError(err.message);
        console.error('Error al cargar datos del admin:', err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchAdminData();
  }, [navigate]);

  const handleLogout = async () => {
    setLoading(true);
    try {
      const { error } = await supabase.auth.signOut();
      if (error) throw error;
      navigate('/login');
    } catch (err) {
      setError(err.message);
      console.error('Error al cerrar sesión:', err.message);
    } finally {
      setLoading(false);
    }
  };

  const renderContent = () => {
    if (loading) return <div className="text-center text-gray-600 text-xl">Cargando datos del panel...</div>;
    if (error) return <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative" role="alert"><strong className="font-bold">¡Error!</strong><span className="block sm:inline"> {error}</span></div>;

    switch (activeTab) {
      case 'overview':
        return (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
            className="bg-white rounded-xl shadow-lg p-8"
          >
            <h2 className="text-3xl font-bold text-gray-800 mb-6">Resumen del Panel de Administración</h2>
            <p className="text-gray-700 text-lg">
              Bienvenido al panel de administración de IQ geoSpatial Technology. Desde aquí puedes gestionar usuarios, software, servicios y más.
            </p>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mt-8">
              <div className="bg-blue-50 rounded-lg p-6 flex items-center gap-4">
                <Users className="w-10 h-10 text-blue-600" />
                <div>
                  <p className="text-gray-600">Total Usuarios</p>
                  <p className="text-3xl font-bold text-blue-800">{users.length}</p>
                </div>
              </div>
              <div className="bg-green-50 rounded-lg p-6 flex items-center gap-4">
                <Package className="w-10 h-10 text-green-600" />
                <div>
                  <p className="text-gray-600">Software Publicado</p>
                  <p className="text-3xl font-bold text-green-800">{softwareProducts.length}</p>
                </div>
              </div>
              <div className="bg-purple-50 rounded-lg p-6 flex items-center gap-4">
                <Settings className="w-10 h-10 text-purple-600" />
                <div>
                  <p className="text-gray-600">Servicios Ofrecidos</p>
                  <p className="text-3xl font-bold text-purple-800">{servicesOffered.length}</p>
                </div>
              </div>
            </div>
          </motion.div>
        );
      case 'users':
        return (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
            className="bg-white rounded-xl shadow-lg p-8"
          >
            <h2 className="text-3xl font-bold text-gray-800 mb-6">Gestión de Usuarios</h2>
            <button className="mb-6 px-6 py-3 bg-blue-600 text-white rounded-lg flex items-center gap-2 hover:bg-blue-700 transition-colors">
              <PlusCircle size={20} /> Añadir Nuevo Usuario
            </button>
            <div className="overflow-x-auto">
              <table className="min-w-full bg-white rounded-lg overflow-hidden">
                <thead className="bg-gray-100">
                  <tr>
                    <th className="py-3 px-6 text-left text-gray-600 font-semibold">ID</th>
                    <th className="py-3 px-6 text-left text-gray-600 font-semibold">Email</th>
                    <th className="py-3 px-6 text-left text-gray-600 font-semibold">Rol</th>
                    <th className="py-3 px-6 text-left text-gray-600 font-semibold">Acciones</th>
                  </tr>
                </thead>
                <tbody>
                  {users.map((user) => (
                    <tr key={user.id} className="border-b border-gray-200 hover:bg-gray-50">
                      <td className="py-4 px-6">{user.id.substring(0, 8)}...</td>
                      <td className="py-4 px-6">{user.email}</td>
                      <td className="py-4 px-6">{user.role || 'user'}</td> {/* Asumiendo que el rol está en la tabla users */}
                      <td className="py-4 px-6 flex gap-2">
                        <button className="text-blue-600 hover:text-blue-800"><Edit size={20} /></button>
                        <button className="text-red-600 hover:text-red-800"><Trash2 size={20} /></button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </motion.div>
        );
      case 'software':
        return (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
            className="bg-white rounded-xl shadow-lg p-8"
          >
            <h2 className="text-3xl font-bold text-gray-800 mb-6">Gestión de Software</h2>
            <button className="mb-6 px-6 py-3 bg-blue-600 text-white rounded-lg flex items-center gap-2 hover:bg-blue-700 transition-colors">
              <PlusCircle size={20} /> Añadir Nuevo Software
            </button>
            <div className="overflow-x-auto">
              <table className="min-w-full bg-white rounded-lg overflow-hidden">
                <thead className="bg-gray-100">
                  <tr>
                    <th className="py-3 px-6 text-left text-gray-600 font-semibold">ID</th>
                    <th className="py-3 px-6 text-left text-gray-600 font-semibold">Nombre</th>
                    <th className="py-3 px-6 text-left text-gray-600 font-semibold">Acciones</th>
                  </tr>
                </thead>
                <tbody>
                  {softwareProducts.map((software) => (
                    <tr key={software.id} className="border-b border-gray-200 hover:bg-gray-50">
                      <td className="py-4 px-6">{software.id.substring(0, 8)}...</td>
                      <td className="py-4 px-6">{software.name}</td>
                      <td className="py-4 px-6 flex gap-2">
                        <button className="text-blue-600 hover:text-blue-800"><Edit size={20} /></button>
                        <button className="text-red-600 hover:text-red-800"><Trash2 size={20} /></button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </motion.div>
        );
      case 'services':
        return (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
            className="bg-white rounded-xl shadow-lg p-8"
          >
            <h2 className="text-3xl font-bold text-gray-800 mb-6">Gestión de Servicios</h2>
            <button className="mb-6 px-6 py-3 bg-blue-600 text-white rounded-lg flex items-center gap-2 hover:bg-blue-700 transition-colors">
              <PlusCircle size={20} /> Añadir Nuevo Servicio
            </button>
            <div className="overflow-x-auto">
              <table className="min-w-full bg-white rounded-lg overflow-hidden">
                <thead className="bg-gray-100">
                  <tr>
                    <th className="py-3 px-6 text-left text-gray-600 font-semibold">ID</th>
                    <th className="py-3 px-6 text-left text-gray-600 font-semibold">Título</th>
                    <th className="py-3 px-6 text-left text-gray-600 font-semibold">Acciones</th>
                  </tr>
                </thead>
                <tbody>
                  {servicesOffered.map((service) => (
                    <tr key={service.id} className="border-b border-gray-200 hover:bg-gray-50">
                      <td className="py-4 px-6">{service.id.substring(0, 8)}...</td>
                      <td className="py-4 px-6">{service.title}</td>
                      <td className="py-4 px-6 flex gap-2">
                        <button className="text-blue-600 hover:text-blue-800"><Edit size={20} /></button>
                        <button className="text-red-600 hover:text-red-800"><Trash2 size={20} /></button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </motion.div>
        );
      default:
        return null;
    }
  };

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.7 }}
      className="py-12"
    >
      <h1 className="text-4xl font-extrabold text-center text-gray-900 mb-10 bg-clip-text text-transparent bg-gradient-to-r from-blue-700 to-purple-600">
        Panel de Administración
      </h1>

      <div className="flex flex-col lg:flex-row gap-8">
        {/* Sidebar de Navegación */}
        <motion.div
          className="lg:w-1/4 bg-white rounded-xl shadow-lg p-6 h-fit sticky top-28"
          initial={{ opacity: 0, x: -50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.5, delay: 0.2 }}
        >
          <nav className="space-y-4">
            <button
              onClick={() => setActiveTab('overview')}
              className={`w-full flex items-center gap-3 px-4 py-3 rounded-lg font-medium transition-colors duration-300 ${
                activeTab === 'overview' ? 'bg-blue-600 text-white shadow-md' : 'text-gray-700 hover:bg-gray-100'
              }`}
            >
              <LayoutDashboard size={20} /> Resumen
            </button>
            <button
              onClick={() => setActiveTab('users')}
              className={`w-full flex items-center gap-3 px-4 py-3 rounded-lg font-medium transition-colors duration-300 ${
                activeTab === 'users' ? 'bg-blue-600 text-white shadow-md' : 'text-gray-700 hover:bg-gray-100'
              }`}
            >
              <Users size={20} /> Usuarios
            </button>
            <button
              onClick={() => setActiveTab('software')}
              className={`w-full flex items-center gap-3 px-4 py-3 rounded-lg font-medium transition-colors duration-300 ${
                activeTab === 'software' ? 'bg-blue-600 text-white shadow-md' : 'text-gray-700 hover:bg-gray-100'
              }`}
            >
              <Package size={20} /> Software
            </button>
            <button
              onClick={() => setActiveTab('services')}
              className={`w-full flex items-center gap-3 px-4 py-3 rounded-lg font-medium transition-colors duration-300 ${
                activeTab === 'services' ? 'bg-blue-600 text-white shadow-md' : 'text-gray-700 hover:bg-gray-100'
              }`}
            >
              <Settings size={20} /> Servicios
            </button>
            <button
              onClick={handleLogout}
              className="w-full flex items-center gap-3 px-4 py-3 rounded-lg font-medium text-red-600 hover:bg-red-50 transition-colors duration-300 mt-6"
            >
              <LogOut size={20} /> Cerrar Sesión
            </button>
          </nav>
        </motion.div>

        {/* Contenido Principal */}
        <div className="lg:w-3/4">
          {renderContent()}
        </div>
      </div>
    </motion.div>
  );
};

export default AdminDashboard;
