import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { Edit, Trash2, PlusCircle } from 'lucide-react';

const UserManagement = ({ initialUsers }) => {
  const [users, setUsers] = useState(initialUsers || []);

  useEffect(() => {
    setUsers(initialUsers);
  }, [initialUsers]);

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
      className="bg-white rounded-xl shadow-lg p-8"
    >
      <h2 className="text-3xl font-bold text-gray-800 mb-6">Gestión de Usuarios</h2>
      <button className="mb-6 px-6 py-3 bg-blue-600 text-white rounded-lg flex items-center gap-2 hover:bg-blue-700 transition-colors disabled:bg-blue-300" disabled>
        <PlusCircle size={20} /> Añadir Usuario (Próximamente)
      </button>
      <div className="overflow-x-auto">
        <table className="min-w-full bg-white rounded-lg overflow-hidden">
          <thead className="bg-gray-100">
            <tr>
              <th className="py-3 px-6 text-left text-gray-600 font-semibold">Email</th>
              <th className="py-3 px-6 text-left text-gray-600 font-semibold">Rol</th>
              <th className="py-3 px-6 text-left text-gray-600 font-semibold">Registrado el</th>
              <th className="py-3 px-6 text-left text-gray-600 font-semibold">Acciones</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user) => (
              <tr key={user.id} className="border-b border-gray-200 hover:bg-gray-50">
                <td className="py-4 px-6">{user.email}</td>
                <td className="py-4 px-6">{user.role || 'user'}</td>
                <td className="py-4 px-6">{new Date(user.created_at).toLocaleDateString()}</td>
                <td className="py-4 px-6 flex gap-2">
                  <button className="text-gray-400 cursor-not-allowed"><Edit size={20} /></button>
                  <button className="text-gray-400 cursor-not-allowed"><Trash2 size={20} /></button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </motion.div>
  );
};

export default UserManagement;
